import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on port 5000
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = 5000;
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { z } from "zod";
import { 
  insertUserSchema, 
  insertProductSchema,
  insertCategorySchema,
  insertCartItemSchema,
  insertOrderSchema,
  insertOrderItemSchema,
  insertReviewSchema
} from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  // Prefix all routes with /api
  const apiRouter = "/api";

  // Auth Routes
  app.post(`${apiRouter}/auth/register`, async (req: Request, res: Response) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(409).json({ message: "User with this email already exists" });
      }

      const newUser = await storage.createUser(userData);

      // Don't return the password
      const { password, ...userWithoutPassword } = newUser;

      return res.status(201).json(userWithoutPassword);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid user data", errors: error.errors });
      }
      return res.status(500).json({ message: "Failed to register user" });
    }
  });

  app.post(`${apiRouter}/auth/login`, async (req: Request, res: Response) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ message: "Email and password are required" });
      }

      const user = await storage.getUserByEmail(email);

      if (!user || user.password !== password) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Don't return the password
      const { password: _, ...userWithoutPassword } = user;

      return res.status(200).json(userWithoutPassword);
    } catch (error) {
      return res.status(500).json({ message: "Login failed" });
    }
  });

  // Category Routes
  app.get(`${apiRouter}/categories`, async (_req: Request, res: Response) => {
    try {
      const categories = await storage.getCategories();
      return res.status(200).json(categories);
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  app.get(`${apiRouter}/categories/:slug`, async (req: Request, res: Response) => {
    try {
      const { slug } = req.params;
      const category = await storage.getCategoryBySlug(slug);

      if (!category) {
        return res.status(404).json({ message: "Category not found" });
      }

      return res.status(200).json(category);
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch category" });
    }
  });

  // Product Routes
  app.get(`${apiRouter}/products`, async (req: Request, res: Response) => {
    try {
      const { 
        limit = "12", 
        offset = "0", 
        category, 
        featured, 
        search,
        minPrice,
        maxPrice,
        sort 
      } = req.query;

      const options: {
        limit?: number;
        offset?: number;
        categoryId?: number;
        featured?: boolean;
        search?: string;
        minPrice?: number;
        maxPrice?: number;
        sort?: string;
      } = {
        limit: parseInt(limit as string),
        offset: parseInt(offset as string)
      };

      // Add optional filters if provided
      if (category) {
        const categoryObj = await storage.getCategoryBySlug(category as string);
        if (categoryObj) {
          options.categoryId = categoryObj.id;
        }
      }

      if (featured !== undefined) {
        options.featured = featured === 'true';
      }

      if (search) {
        options.search = search as string;
      }

      if (minPrice) {
        options.minPrice = parseFloat(minPrice as string);
      }

      if (maxPrice) {
        options.maxPrice = parseFloat(maxPrice as string);
      }

      if (sort) {
        options.sort = sort as string;
      }

      const { products, total } = await storage.getProducts(options);

      return res.status(200).json({
        products,
        pagination: {
          total,
          limit: options.limit,
          offset: options.offset
        }
      });
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  app.get(`${apiRouter}/products/:slug`, async (req: Request, res: Response) => {
    try {
      const { slug } = req.params;
      const product = await storage.getProductBySlug(slug);

      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }

      return res.status(200).json(product);
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch product" });
    }
  });

  // Featured Products
  app.get(`${apiRouter}/products/featured`, async (_req: Request, res: Response) => {
    try {
      const { products } = await storage.getProducts({ featured: true, limit: 8 });
      return res.status(200).json(products);
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch featured products" });
    }
  });

  // Product Reviews
  app.get(`${apiRouter}/products/:id/reviews`, async (req: Request, res: Response) => {
    try {
      const { id } = req.params;
      const productId = parseInt(id);

      if (isNaN(productId)) {
        return res.status(400).json({ message: "Invalid product ID" });
      }

      const product = await storage.getProduct(productId);

      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }

      const reviews = await storage.getReviews(productId);
      return res.status(200).json(reviews);
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch reviews" });
    }
  });

  app.post(`${apiRouter}/products/:id/reviews`, async (req: Request, res: Response) => {
    try {
      const { id } = req.params;
      const productId = parseInt(id);

      if (isNaN(productId)) {
        return res.status(400).json({ message: "Invalid product ID" });
      }

      const product = await storage.getProduct(productId);

      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }

      const reviewData = insertReviewSchema.parse({
        ...req.body,
        productId
      });

      const newReview = await storage.createReview(reviewData);
      return res.status(201).json(newReview);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid review data", errors: error.errors });
      }
      return res.status(500).json({ message: "Failed to create review" });
    }
  });

  // Cart Routes
  app.get(`${apiRouter}/cart/:userId`, async (req: Request, res: Response) => {
    try {
      const { userId } = req.params;
      const userIdNum = parseInt(userId);

      if (isNaN(userIdNum)) {
        return res.status(400).json({ message: "Invalid user ID" });
      }

      const user = await storage.getUser(userIdNum);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const cartItems = await storage.getCartItems(userIdNum);

      // Get product details for each cart item
      const cartItemsWithDetails = await Promise.all(
        cartItems.map(async (item) => {
          const product = await storage.getProduct(item.productId);
          return {
            ...item,
            product
          };
        })
      );

      return res.status(200).json(cartItemsWithDetails);
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch cart items" });
    }
  });

  app.post(`${apiRouter}/cart`, async (req: Request, res: Response) => {
    try {
      const cartItemData = insertCartItemSchema.parse(req.body);

      // Check if user exists
      const user = await storage.getUser(cartItemData.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Check if product exists
      const product = await storage.getProduct(cartItemData.productId);
      if (!product) {
        return res.status(404).json({ message: "Product not found" });
      }

      // Check if product is in stock
      if (product.stock < cartItemData.quantity) {
        return res.status(400).json({ message: "Not enough stock available" });
      }

      // Check if item already in cart
      const existingItem = await storage.getCartItemByUserAndProduct(
        cartItemData.userId,
        cartItemData.productId
      );

      if (existingItem) {
        // Update quantity
        const updatedItem = await storage.updateCartItem(
          existingItem.id,
          { quantity: existingItem.quantity + cartItemData.quantity }
        );

        return res.status(200).json(updatedItem);
      } else {
        // Create new cart item
        const newCartItem = await storage.createCartItem(cartItemData);
        return res.status(201).json(newCartItem);
      }
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid cart item data", errors: error.errors });
      }
      return res.status(500).json({ message: "Failed to add item to cart" });
    }
  });

  app.put(`${apiRouter}/cart/:id`, async (req: Request, res: Response) => {
    try {
      const { id } = req.params;
      const cartItemId = parseInt(id);

      if (isNaN(cartItemId)) {
        return res.status(400).json({ message: "Invalid cart item ID" });
      }

      const { quantity } = req.body;

      if (typeof quantity !== 'number' || quantity < 1) {
        return res.status(400).json({ message: "Quantity must be a positive number" });
      }

      const cartItem = await storage.getCartItem(cartItemId);

      if (!cartItem) {
        return res.status(404).json({ message: "Cart item not found" });
      }

      // Check if product has enough stock
      const product = await storage.getProduct(cartItem.productId);
      if (!product || product.stock < quantity) {
        return res.status(400).json({ message: "Not enough stock available" });
      }

      const updatedCartItem = await storage.updateCartItem(cartItemId, { quantity });
      return res.status(200).json(updatedCartItem);
    } catch (error) {
      return res.status(500).json({ message: "Failed to update cart item" });
    }
  });

  app.delete(`${apiRouter}/cart/:id`, async (req: Request, res: Response) => {
    try {
      const { id } = req.params;
      const cartItemId = parseInt(id);

      if (isNaN(cartItemId)) {
        return res.status(400).json({ message: "Invalid cart item ID" });
      }

      const cartItem = await storage.getCartItem(cartItemId);

      if (!cartItem) {
        return res.status(404).json({ message: "Cart item not found" });
      }

      await storage.deleteCartItem(cartItemId);
      return res.status(200).json({ message: "Cart item removed successfully" });
    } catch (error) {
      return res.status(500).json({ message: "Failed to remove cart item" });
    }
  });

  app.delete(`${apiRouter}/cart/user/:userId`, async (req: Request, res: Response) => {
    try {
      const { userId } = req.params;
      const userIdNum = parseInt(userId);

      if (isNaN(userIdNum)) {
        return res.status(400).json({ message: "Invalid user ID" });
      }

      const user = await storage.getUser(userIdNum);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      await storage.clearCart(userIdNum);
      return res.status(200).json({ message: "Cart cleared successfully" });
    } catch (error) {
      return res.status(500).json({ message: "Failed to clear cart" });
    }
  });

  // Order Routes
  app.get(`${apiRouter}/orders/:userId`, async (req: Request, res: Response) => {
    try {
      const { userId } = req.params;
      const userIdNum = parseInt(userId);

      if (isNaN(userIdNum)) {
        return res.status(400).json({ message: "Invalid user ID" });
      }

      const user = await storage.getUser(userIdNum);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const orders = await storage.getOrders(userIdNum);

      // Get order items for each order
      const ordersWithItems = await Promise.all(
        orders.map(async (order) => {
          const items = await storage.getOrderItems(order.id);
          return {
            ...order,
            items
          };
        })
      );

      return res.status(200).json(ordersWithItems);
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch orders" });
    }
  });

  app.get(`${apiRouter}/orders/:userId/:orderId`, async (req: Request, res: Response) => {
    try {
      const { userId, orderId } = req.params;
      const userIdNum = parseInt(userId);
      const orderIdNum = parseInt(orderId);

      if (isNaN(userIdNum) || isNaN(orderIdNum)) {
        return res.status(400).json({ message: "Invalid user ID or order ID" });
      }

      const user = await storage.getUser(userIdNum);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const order = await storage.getOrder(orderIdNum);

      if (!order) {
        return res.status(404).json({ message: "Order not found" });
      }

      if (order.userId !== userIdNum) {
        return res.status(403).json({ message: "Unauthorized access to order" });
      }

      const orderItems = await storage.getOrderItems(orderIdNum);

      // Get product details for each order item
      const orderItemsWithDetails = await Promise.all(
        orderItems.map(async (item) => {
          const product = await storage.getProduct(item.productId);
          return {
            ...item,
            product
          };
        })
      );

      return res.status(200).json({
        ...order,
        items: orderItemsWithDetails
      });
    } catch (error) {
      return res.status(500).json({ message: "Failed to fetch order" });
    }
  });

  app.post(`${apiRouter}/orders`, async (req: Request, res: Response) => {
    try {
      const orderData = insertOrderSchema.parse(req.body);

      // Check if user exists
      const user = await storage.getUser(orderData.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Get cart items
      const cartItems = await storage.getCartItems(orderData.userId);

      if (cartItems.length === 0) {
        return res.status(400).json({ message: "Cart is empty" });
      }

      // Validate cart items and calculate total
      let total = 0;
      const validatedItems = await Promise.all(
        cartItems.map(async (item) => {
          const product = await storage.getProduct(item.productId);

          if (!product) {
            throw new Error(`Product with ID ${item.productId} not found`);
          }

          if (product.stock < item.quantity) {
            throw new Error(`Not enough stock for ${product.name}`);
          }

          const price = product.salePrice || product.price;
          total += price * item.quantity;

          return {
            productId: item.productId,
            quantity: item.quantity,
            price,
            customizations: item.customizations
          };
        })
      );

      // Create order
      const newOrder = await storage.createOrder({
        ...orderData,
        total,
        status: "pending"
      });

      // Create order items
      const orderItems = await Promise.all(
        validatedItems.map(item => 
          storage.createOrderItem({
            orderId: newOrder.id,
            productId: item.productId,
            quantity: item.quantity,
            price: item.price,
            customizations: item.customizations
          })
        )
      );

      // Update product stock
      await Promise.all(
        validatedItems.map(async (item) => {
          const product = await storage.getProduct(item.productId);
          if (product) {
            await storage.updateProduct(product.id, {
              stock: product.stock - item.quantity
            });
          }
        })
      );

      // Clear cart
      await storage.clearCart(orderData.userId);

      return res.status(201).json({
        ...newOrder,
        items: orderItems
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid order data", errors: error.errors });
      }
      if (error instanceof Error) {
        return res.status(400).json({ message: error.message });
      }
      return res.status(500).json({ message: "Failed to create order" });
    }
  });

  // Setup HTTP server
  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import {
  users, User, InsertUser, 
  products, Product, InsertProduct,
  categories, Category, InsertCategory,
  cartItems, CartItem, InsertCartItem,
  orders, Order, InsertOrder,
  orderItems, OrderItem, InsertOrderItem,
  reviews, Review, InsertReview
} from "@shared/schema";

export interface IStorage {
  // User operations
  getUser(id: number): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: number, userData: Partial<InsertUser>): Promise<User | undefined>;

  // Category operations
  getCategories(): Promise<Category[]>;
  getCategory(id: number): Promise<Category | undefined>;
  getCategoryBySlug(slug: string): Promise<Category | undefined>;
  createCategory(category: InsertCategory): Promise<Category>;

  // Product operations
  getProducts(options?: { 
    limit?: number, 
    offset?: number, 
    categoryId?: number,
    featured?: boolean,
    search?: string,
    minPrice?: number,
    maxPrice?: number,
    sort?: string 
  }): Promise<{ products: Product[], total: number }>;
  getProduct(id: number): Promise<Product | undefined>;
  getProductBySlug(slug: string): Promise<Product | undefined>;
  createProduct(product: InsertProduct): Promise<Product>;
  updateProduct(id: number, productData: Partial<InsertProduct>): Promise<Product | undefined>;

  // Cart operations
  getCartItems(userId: number): Promise<CartItem[]>;
  getCartItem(id: number): Promise<CartItem | undefined>;
  getCartItemByUserAndProduct(userId: number, productId: number): Promise<CartItem | undefined>;
  createCartItem(cartItem: InsertCartItem): Promise<CartItem>;
  updateCartItem(id: number, cartItemData: Partial<InsertCartItem>): Promise<CartItem | undefined>;
  deleteCartItem(id: number): Promise<boolean>;
  clearCart(userId: number): Promise<boolean>;

  // Order operations
  getOrders(userId: number): Promise<Order[]>;
  getOrder(id: number): Promise<Order | undefined>;
  createOrder(order: InsertOrder): Promise<Order>;
  updateOrder(id: number, orderData: Partial<InsertOrder>): Promise<Order | undefined>;

  // Order item operations
  getOrderItems(orderId: number): Promise<OrderItem[]>;
  createOrderItem(orderItem: InsertOrderItem): Promise<OrderItem>;

  // Review operations
  getReviews(productId: number): Promise<Review[]>;
  createReview(review: InsertReview): Promise<Review>;
}

export class MemStorage implements IStorage {
  private users: Map<number, User>;
  private categories: Map<number, Category>;
  private products: Map<number, Product>;
  private cartItems: Map<number, CartItem>;
  private orders: Map<number, Order>;
  private orderItems: Map<number, OrderItem>;
  private reviews: Map<number, Review>;

  private currentUserIds: number;
  private currentCategoryIds: number;
  private currentProductIds: number;
  private currentCartItemIds: number;
  private currentOrderIds: number;
  private currentOrderItemIds: number;
  private currentReviewIds: number;

  constructor() {
    this.users = new Map();
    this.categories = new Map();
    this.products = new Map();
    this.cartItems = new Map();
    this.orders = new Map();
    this.orderItems = new Map();
    this.reviews = new Map();

    this.currentUserIds = 1;
    this.currentCategoryIds = 1;
    this.currentProductIds = 1;
    this.currentCartItemIds = 1;
    this.currentOrderIds = 1;
    this.currentOrderItemIds = 1;
    this.currentReviewIds = 1;

    // Seed initial data
    this.seedData();
  }

  // User operations
  async getUser(id: number): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username.toLowerCase() === username.toLowerCase()
    );
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.email.toLowerCase() === email.toLowerCase()
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = this.currentUserIds++;
    const user: User = { ...insertUser, id, wishlistIds: [], createdAt: new Date() };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: number, userData: Partial<InsertUser>): Promise<User | undefined> {
    const existingUser = await this.getUser(id);
    if (!existingUser) return undefined;

    const updatedUser = { ...existingUser, ...userData };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return Array.from(this.categories.values());
  }

  async getCategory(id: number): Promise<Category | undefined> {
    return this.categories.get(id);
  }

  async getCategoryBySlug(slug: string): Promise<Category | undefined> {
    return Array.from(this.categories.values()).find(
      (category) => category.slug === slug
    );
  }

  async createCategory(insertCategory: InsertCategory): Promise<Category> {
    const id = this.currentCategoryIds++;
    const category: Category = { ...insertCategory, id };
    this.categories.set(id, category);
    return category;
  }

  // Product operations
  async getProducts(options: { 
    limit?: number, 
    offset?: number, 
    categoryId?: number,
    featured?: boolean,
    search?: string,
    minPrice?: number,
    maxPrice?: number,
    sort?: string 
  } = {}): Promise<{ products: Product[], total: number }> {
    let filteredProducts = Array.from(this.products.values());

    // Apply filters
    if (options.categoryId !== undefined) {
      filteredProducts = filteredProducts.filter(p => p.categoryId === options.categoryId);
    }

    if (options.featured !== undefined) {
      filteredProducts = filteredProducts.filter(p => p.featured === options.featured);
    }

    if (options.search !== undefined) {
      const searchTerm = options.search.toLowerCase();
      filteredProducts = filteredProducts.filter(p => 
        p.name.toLowerCase().includes(searchTerm) || 
        (p.description && p.description.toLowerCase().includes(searchTerm))
      );
    }

    if (options.minPrice !== undefined) {
      filteredProducts = filteredProducts.filter(p => p.price >= options.minPrice!);
    }

    if (options.maxPrice !== undefined) {
      filteredProducts = filteredProducts.filter(p => p.price <= options.maxPrice!);
    }

    // Apply sorting
    if (options.sort) {
      switch (options.sort) {
        case 'price_asc':
          filteredProducts.sort((a, b) => a.price - b.price);
          break;
        case 'price_desc':
          filteredProducts.sort((a, b) => b.price - a.price);
          break;
        case 'name_asc':
          filteredProducts.sort((a, b) => a.name.localeCompare(b.name));
          break;
        case 'name_desc':
          filteredProducts.sort((a, b) => b.name.localeCompare(a.name));
          break;
        case 'newest':
          filteredProducts.sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
          break;
        case 'rating':
          filteredProducts.sort((a, b) => b.rating - a.rating);
          break;
      }
    }

    const total = filteredProducts.length;

    // Apply pagination
    if (options.offset !== undefined && options.limit !== undefined) {
      filteredProducts = filteredProducts.slice(options.offset, options.offset + options.limit);
    } else if (options.limit !== undefined) {
      filteredProducts = filteredProducts.slice(0, options.limit);
    }

    return { products: filteredProducts, total };
  }

  async getProduct(id: number): Promise<Product | undefined> {
    return this.products.get(id);
  }

  async getProductBySlug(slug: string): Promise<Product | undefined> {
    return Array.from(this.products.values()).find(
      (product) => product.slug === slug
    );
  }

  async createProduct(insertProduct: InsertProduct): Promise<Product> {
    const id = this.currentProductIds++;
    const product: Product = { 
      ...insertProduct, 
      id, 
      rating: 0,
      reviewCount: 0,
      createdAt: new Date()
    };
    this.products.set(id, product);
    return product;
  }

  async updateProduct(id: number, productData: Partial<InsertProduct>): Promise<Product | undefined> {
    const existingProduct = await this.getProduct(id);
    if (!existingProduct) return undefined;

    const updatedProduct = { ...existingProduct, ...productData };
    this.products.set(id, updatedProduct);
    return updatedProduct;
  }

  // Cart operations
  async getCartItems(userId: number): Promise<CartItem[]> {
    return Array.from(this.cartItems.values()).filter(
      (item) => item.userId === userId
    );
  }

  async getCartItem(id: number): Promise<CartItem | undefined> {
    return this.cartItems.get(id);
  }

  async getCartItemByUserAndProduct(userId: number, productId: number): Promise<CartItem | undefined> {
    return Array.from(this.cartItems.values()).find(
      (item) => item.userId === userId && item.productId === productId
    );
  }

  async createCartItem(insertCartItem: InsertCartItem): Promise<CartItem> {
    const id = this.currentCartItemIds++;
    const cartItem: CartItem = { ...insertCartItem, id, createdAt: new Date() };
    this.cartItems.set(id, cartItem);
    return cartItem;
  }

  async updateCartItem(id: number, cartItemData: Partial<InsertCartItem>): Promise<CartItem | undefined> {
    const existingCartItem = await this.getCartItem(id);
    if (!existingCartItem) return undefined;

    const updatedCartItem = { ...existingCartItem, ...cartItemData };
    this.cartItems.set(id, updatedCartItem);
    return updatedCartItem;
  }

  async deleteCartItem(id: number): Promise<boolean> {
    return this.cartItems.delete(id);
  }

  async clearCart(userId: number): Promise<boolean> {
    const userCartItems = await this.getCartItems(userId);
    userCartItems.forEach(item => {
      this.cartItems.delete(item.id);
    });
    return true;
  }

  // Order operations
  async getOrders(userId: number): Promise<Order[]> {
    return Array.from(this.orders.values()).filter(
      (order) => order.userId === userId
    );
  }

  async getOrder(id: number): Promise<Order | undefined> {
    return this.orders.get(id);
  }

  async createOrder(insertOrder: InsertOrder): Promise<Order> {
    const id = this.currentOrderIds++;
