import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { apiRequest } from '@/lib/queryClient';
import { useAuth } from './AuthContext';
import { useToast } from '@/hooks/use-toast';
import { Product } from '@shared/schema';

export interface CartItem {
  id: number;
  userId: number;
  productId: number;
  quantity: number;
  product?: Product;
  customizations?: any;
}

interface CartContextType {
  items: CartItem[];
  loading: boolean;
  error: string | null;
  addItem: (productId: number, quantity: number, customizations?: any) => Promise<void>;
  updateItem: (id: number, quantity: number) => Promise<void>;
  removeItem: (id: number) => Promise<void>;
  clearCart: () => Promise<void>;
  itemCount: number;
  subtotal: number;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

export function CartProvider({ children }: { children: ReactNode }) {
  const [items, setItems] = useState<CartItem[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();

  // Calculate derived values
  const itemCount = items.reduce((sum, item) => sum + item.quantity, 0);
  const subtotal = items.reduce((sum, item) => {
    if (item.product) {
      const price = item.product.salePrice || item.product.price;
      return sum + (price * item.quantity);
    }
    return sum;
  }, 0);

  // Fetch cart whenever user changes
  useEffect(() => {
    if (isAuthenticated && user) {
      fetchCart();
    } else {
      setItems([]);
    }
  }, [isAuthenticated, user]);

  const fetchCart = async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError(null);

      const response = await apiRequest('GET', `/api/cart/${user.id}`);
      const cartItems = await response.json();

      setItems(cartItems);
    } catch (error) {
      console.error('Failed to fetch cart:', error);
      setError('Failed to load your cart. Please try again.');

      toast({
        title: 'Cart Error',
        description: 'Failed to load your cart items.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const addItem = async (productId: number, quantity: number, customizations?: any) => {
    if (!user) {
      toast({
        title: 'Authentication Required',
        description: 'Please log in to add items to your cart.',
        variant: 'destructive',
      });
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const payload = {
        userId: user.id,
        productId,
        quantity,
        customizations
      };

      const response = await apiRequest('POST', '/api/cart', payload);
      const updatedItem = await response.json();

      // Update local cart state
      fetchCart();

      toast({
        title: 'Item Added',
        description: 'Product has been added to your cart.',
      });
    } catch (error) {
      console.error('Failed to add item to cart:', error);
      setError('Failed to add item to cart. Please try again.');

      toast({
        title: 'Cart Error',
        description: 'Failed to add item to your cart.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const updateItem = async (id: number, quantity: number) => {
    try {
      setLoading(true);
      setError(null);

      await apiRequest('PUT', `/api/cart/${id}`, { quantity });

      // Update local cart state
      fetchCart();

      toast({
        title: 'Cart Updated',
        description: 'Your cart has been updated.',
      });
    } catch (error) {
      console.error('Failed to update cart item:', error);
      setError('Failed to update cart. Please try again.');

      toast({
        title: 'Cart Error',
        description: 'Failed to update your cart.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const removeItem = async (id: number) => {
    try {
      setLoading(true);
      setError(null);

      await apiRequest('DELETE', `/api/cart/${id}`);

      // Update local cart state
      fetchCart();

      toast({
        title: 'Item Removed',
        description: 'Item has been removed from your cart.',
      });
    } catch (error) {
      console.error('Failed to remove cart item:', error);
      setError('Failed to remove item from cart. Please try again.');

      toast({
        title: 'Cart Error',
        description: 'Failed to remove item from your cart.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const clearCart = async () => {
    if (!user) return;

    try {
      setLoading(true);
      setError(null);

      await apiRequest('DELETE', `/api/cart/user/${user.id}`);

      // Update local cart state
      setItems([]);

      toast({
        title: 'Cart Cleared',
        description: 'Your cart has been cleared.',
      });
    } catch (error) {
      console.error('Failed to clear cart:', error);
      setError('Failed to clear cart. Please try again.');

      toast({
        title: 'Cart Error',
        description: 'Failed to clear your cart.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <CartContext.Provider value={{
      items,
      loading,
      error,
      addItem,
      updateItem,
      removeItem,
      clearCart,
      itemCount,
      subtotal
    }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
}

client/src/context/CurrencyContext.tsx.content:
import { createContext, useState, useContext, useCallback, ReactNode, useEffect } from 'react';

type Currency = 'USD' | 'INR' | 'EUR' | 'GBP' | 'JPY' | 'AUD' | 'CAD';

interface CurrencyContextType {
  currency: Currency;
  setCurrency: (currency: Currency) => void;
  formatPrice: (price: number) => string;
  convertPrice: (price: number) => number;
  exchangeRate: number;
  currencySymbol: string;
  availableCurrencies: Array<{code: Currency, symbol: string, name: string}>;
}

const currencySymbols: Record<Currency, string> = {
  USD: '$',
  INR: 'â‚¹',
  EUR: 'â‚¬',
  GBP: 'Â£',
  JPY: 'Â¥',
  AUD: 'A$',
  CAD: 'C$'
};

const currencyNames: Record<Currency, string> = {
  USD: 'US Dollar',
  INR: 'Indian Rupee',
  EUR: 'Euro',
  GBP: 'British Pound',
  JPY: 'Japanese Yen',
  AUD: 'Australian Dollar',
  CAD: 'Canadian Dollar'
};

const exchangeRates: Record<Currency, number> = {
  USD: 1.0,
  INR: 83.36, // 1 USD = 83.36 INR
  EUR: 0.93,  // 1 USD = 0.93 EUR
  GBP: 0.80,  // 1 USD = 0.80 GBP
  JPY: 154.41, // 1 USD = 154.41 JPY
  AUD: 1.52, // 1 USD = 1.52 AUD
  CAD: 1.37  // 1 USD = 1.37 CAD
};

const CurrencyContext = createContext<CurrencyContextType | undefined>(undefined);

export function CurrencyProvider({ children }: { children: ReactNode }) {
  // Default to INR as preferred currency
  const [currency, setCurrency] = useState<Currency>('INR');

  // Set default currency from localStorage on initial load
  useEffect(() => {
    const savedCurrency = localStorage.getItem('preferredCurrency');
    if (savedCurrency && Object.keys(currencySymbols).includes(savedCurrency)) {
      setCurrency(savedCurrency as Currency);
    }
  }, []);

  // Save currency preference to localStorage when changed
  useEffect(() => {
    localStorage.setItem('preferredCurrency', currency);
  }, [currency]);

  const exchangeRate = exchangeRates[currency];
  const currencySymbol = currencySymbols[currency];

  // Convert price from USD to selected currency
  const convertPrice = useCallback((price: number): number => {
    return price * exchangeRate;
  }, [exchangeRate]);

  // Format price according to currency conventions
  const formatPrice = useCallback((price: number) => {
    const convertedPrice = price * exchangeRate;

    // Japanese Yen doesn't use decimal places
    if (currency === 'JPY') {
      return `${currencySymbol}${Math.round(convertedPrice).toLocaleString()}`;
    }

    // Indian format uses different thousands separators
    if (currency === 'INR') {
      // Convert to Indian numbering system (lakhs, crores)
      const formatter = new Intl.NumberFormat('en-IN', {
        style: 'currency',
        currency: 'INR',
        maximumFractionDigits: 2
      });
      return formatter.format(convertedPrice);
    }

    // Default formatting for other currencies
    return `${currencySymbol}${convertedPrice.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ',')}`;
  }, [currency, exchangeRate, currencySymbol]);

  // Create array of available currencies for dropdown
  const availableCurrencies = Object.keys(currencySymbols).map((code) => ({
    code: code as Currency,
    symbol: currencySymbols[code as Currency],
    name: currencyNames[code as Currency]
  }));

  const value = {
    currency,
    setCurrency,
    formatPrice,
    convertPrice,
    exchangeRate,
    currencySymbol,
    availableCurrencies
  };

  return (
    <CurrencyContext.Provider value={value}>
      {children}
    </CurrencyContext.Provider>
  );
}

export function useCurrency() {
  const context = useContext(CurrencyContext);
  if (context === undefined) {
    throw new Error('useCurrency must be used within a CurrencyProvider');
  }
  return context;
}

